<script setup>
import { onMounted } from "vue";
import AOS from "aos";
const boxNumber = 3;

const interests = [
        { title: "Web Development", description: "Building responsive and dynamic websites." },
        { title: "UI/UX Design", description: "Creating user-friendly interfaces with intuitive design." },
        { title: "Data Science", description: "Analyzing and interpreting complex data." },
        { title: "Internet of Things (IoT)", description: "Connecting devices to improve everyday life." }
      ]
    

onMounted(() => {
    AOS.init({
      offset: 10, // offset (in px) from the original trigger point
    duration: 1000, // values from 0 to 3000, with step 50ms
    easing: "ease-out",
    mirror: true,
    once: false,
    anchorPlacement: "top-center"

    }
    );
    AOS.refresh();
})

const getBoxStyle = (index) => {
  if (index % 2 === 0){
      return{
        right : '0%'
      }
    }
    else
    return{
        left: '30%'
    }
};
const getLogoStyle = (index) => {
  if (index % 2 === 0){
      return{
        left : '0'
      }
    }
    else
    return{
        right: '0'
    }
};

</script>


<style >

html,
body {
  margin: 0;
  background: #262938;
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 400vh;
  justify-content: center; 
  font-family: Arial, sans-serif;
}

/* Wrapper to hold the image and page content */
.page-wrapper {
  width: 100%;
  text-align: center;
  position: relative;

  /* overflow: auto; */
}

/* Container for the top image */
.top-image-container {
  width: 82%;
  position: relative;
  margin: auto;
  z-index: 10;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2); 
  border-radius: 15px;
}

/* The image itself */
.top-image {
  position: relative;
  width: 100%;
  height: auto; 
  border-radius: 15px;
}


.text-overlay {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(244, 211, 210, 0.6);
  z-index: 20; /* Ensure it's above the image */
  padding: 20px;
  border-radius: 10px;
  text-align: center;
}

@import url('https://fonts.googleapis.com/css2?family=Lora:wght@400;700&family=Poppins:wght@600&display=swap');

.text-overlay p {
  font-family: 'Lora', serif; 
  font-size: 30px;
  font-weight: 400; 
  color: #262938; 
  letter-spacing: 1px; 
  line-height: 1.4; 
  margin: 0;
  padding: 0;
  text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.2); 
  animation: fadeInUp 1s ease-in-out;
}

.text-overlay p span {
  font-family: 'Poppins', sans-serif; 
  font-size: 48px; 
  font-weight: 600;
  color: #995A5A; 
  text-transform: uppercase;
  letter-spacing: 2px;
}

@keyframes fadeInUp {
  0% {
    opacity: 0;
    transform: translateY(10px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}


.scroll-text-container {
  height: 15%;
  position: absolute;
  left: 50%;
  bottom: 0;
  transform: translateX(-50%);
  text-align: center;
}

.scroll-text p {
  padding-bottom: 10%;
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 500;
  color: #ffffff;
  margin: 0;
  animation: scrollText 3s linear infinite; 
}


@keyframes scrollText {
  0% {
    transform: translateY(10px);
  }
  50% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(10px);
  }
}




.wave {
    position: relative;
    bottom: 0;
    left: 0;
    width: 100%;
    overflow: hidden;
    line-height: 0;
    transform: rotate(180deg);
}

.wave svg {
    position: relative;
    display: block;
    width: calc(106% + 1.3px);
